package JavaPractice;

public class pyramidTriangleUsingNestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Example on pyramid Triangle

		//int k=1;
		for(int i=1;i<5;i++)
		{
		for(int j=1;j<=i;j++)
		{
		System.out.print(j);
		System.out.print("\t");
		//k++;
		}
		System.out.println("");

		}
	}

}
